TVFImplementation.CreateFunctionSQL:
CREATE FUNCTION USR_TVF_APPUSERROLES() RETURNS @SYSROLEAPPUSER TABLE (ID int, APPUSER nvarchar(255), SYSTEMROLE nvarchar(255), SECURITYMODE nvarchar(40), SITEID nvarchar(100), DATEADDED datetime, ADDEDBY nvarchar(128), DATECHANGED datetime, CHANGEDBY nvarchar(128)) AS BEGIN INSERT INTO @SYSROLEAPPUSER (ID, APPUSER, SYSTEMROLE, SECURITYMODE, SITEID, DATEADDED, ADDEDBY, DATECHANGED, CHANGEDBY) SELECT row_number() over (ORDER BY au.DISPLAYNAME asc, sr.NAME asc, s.ID asc) as ID, au.DISPLAYNAME, sr.NAME, srau.SECURITYMODE, s.SITEID, CASE WHEN sraus.ID IS NULL THEN srau.DATEADDED ELSE sraus.DATEADDED END as DATEADDED, CASE WHEN sraus.ID IS NULL THEN sraddedbyau.USERNAME ELSE addedbyau.USERNAME END as ADDEDBY, CASE WHEN sraus.ID IS NULL THEN srau.DATECHANGED ELSE sraus.DATECHANGED END as DATECHANGED, CASE WHEN sraus.ID IS NULL THEN srchangedbyid.USERNAME ELSE changedbyid.USERNAME END as CHANGEDBY FROM dbo.APPUSER au JOIN dbo.SYSTEMROLEAPPUSER srau ON srau.APPUSERID = au.ID JOIN dbo.SYSTEMROLE sr ON sr.ID = srau.SYSTEMROLEID LEFT OUTER JOIN dbo.SYSTEMROLEAPPUSERSITE sraus ON sraus.SYSTEMROLEAPPUSERID = srau.ID LEFT OUTER JOIN dbo.SITE s ON s.ID = sraus.SITEID LEFT OUTER JOIN dbo.CHANGEAGENT addedbyau ON addedbyau.ID = sraus.ADDEDBYID LEFT OUTER JOIN dbo.CHANGEAGENT changedbyid ON changedbyid.ID = sraus.CHANGEDBYID LEFT OUTER JOIN dbo.CHANGEAGENT sraddedbyau ON sraddedbyau.ID = srau.ADDEDBYID LEFT OUTER JOIN dbo.CHANGEAGENT srchangedbyid ON srchangedbyid.ID = srau.CHANGEDBYID
RETURN;
END
